snippets.initCode:
let text = new PIXI.Text('Code Cut', {fontFamily: 'Kanit', fontSize: 200, fill: 0xff0000 });

this.pixi.addChild(text);
this.pixi.beginFill(0xff00000);
this.pixi.drawRect(50, 200, 750, 5);

this.rect = this.pixi.getBounds();

this.pixels = this.pixiApp.renderer.extract.pixels(this.pixi);

this.pixi.removeChild(text);
this.pixi.clear();

snippets.tickCode:
this.pixi.clear()
let highest = 0;
for (let x = 0; x < this.rect.width; x += 8) {
  for (let y = 0; y < this.rect.height; y += 8) {
    const index = (y*this.rect.width + x)*4;
    if (this.pixels[index]*255*255 + this.pixels[index+1] * 255 + this.pixels[index+2] == 0) continue;
    this.pixi.beginFill(0xf7931e);
    let offset = Math.max((x+y)/(this.rect.width+this.rect.height)*5-time*5+1.2, 0);
    offset = EASING.easeInOutCubic(offset/6)*6;
    const rOffset = Math.max(2-time+(x+y)/(this.rect.width+this.rect.height)*5, 0);
    this.pixi.drawRect(
      x + 600,
      y + 400 + offset*400 + Math.sin(time*4 + x/this.rect.width*20)*10,
      4 + offset*8 + rOffset*5,
      4 + offset*16 + rOffset*1
    );
  }
}

if (time > nodeTime - 0.2) {
  timeline.scrub(0);
  timeline.play();
}
